# Function to send an email with an encrypted message and image attachment
def send_email(file_path, message, sender_email, smtp_password, receiver_email):
    try:
        # Setup the MIME
        msg = MIMEMultipart()
        msg['From'] = sender_email
        msg['To'] = receiver_email
        msg['Subject'] = "Encrypted Message with Image Attachment"

        # Encrypt the message
        encrypted_message = cipher_suite.encrypt(message.encode()).decode()

        # Attach the encrypted message to the email
        msg.attach(MIMEText(encrypted_message, 'plain'))

        # Attach the image
        if file_path:
            attachment = open(file_path, "rb")
            part = MIMEBase('application', 'octet-stream')
            part.set_payload(attachment.read())
            encoders.encode_base64(part)
            part.add_header('Content-Disposition', f"attachment; filename= {os.path.basename(file_path)}")
            msg.attach(part)
            attachment.close()

        # Setup the server
        server = smtplib.SMTP('smtp.gmail.com', 587)
        server.starttls()
        server.login(sender_email, smtp_password)

        # Send the email
        server.send_message(msg)
        server.quit()

        messagebox.showinfo("Success", "Email sent successfully!")
    except Exception as e:
        messagebox.showerror("Error", f"Failed to send email: {e}")

