# Function to decrypt the encoded message and the message hidden in the image
def decrypt_message(encoded_message, image_file_path):
    try:
        # Decrypt the encoded message
        decrypted_encoded_message = cipher_suite.decrypt(encoded_message.encode()).decode()

        # Extract and decrypt the message from the image
        encrypted_message_from_image = extract_encrypted_message_from_image(image_file_path)
        if not encrypted_message_from_image:
            return decrypted_encoded_message

        decrypted_message = cipher_suite.decrypt(encrypted_message_from_image.encode()).decode()
        return decrypted_message
    except InvalidToken:
        messagebox.showerror("Error", "Failed to decrypt message: Invalid Token")
        return ""
    except Exception as e:
        messagebox.showerror("Error", f"Failed to decrypt message: {e}")
        return ""

